class Hamburger{constructor(){this.hamburgerDiv=document.querySelector("#icon"),this.divs=document.querySelector("#divTop"),this.divHead=document.querySelector(".head"),this.menuMobile=document.querySelector("#menuMobile"),this.hamburgerDiv.addEventListener("click",this.abrirMenu.bind(this)),document.addEventListener("scroll",this.animationHamburguer.bind(this))}abrirMenu(){this.divs.classList.toggle("open"),this.menuMobile.classList.toggle("active")}animationHamburguer(e){e.target.scrollingElement.scrollTop>0?(this.divHead.classList.add("animate"),this.menuMobile.classList.remove("active"),this.divs.classList.remove("open")):this.divHead.classList.remove("animate")}}class Main_new{constructor(){document.addEventListener("scroll",this.animarSkills.bind(this)),this.nodeSkills=document.querySelector(".skillsBar"),this.nodeBas=document.querySelectorAll(".nodeBas"),this.nodeAdv=document.querySelectorAll(".nodeAdv"),this.nodeExp=document.querySelectorAll(".nodeExp")}animarSkills(){this.nodeBas.forEach(e=>{this.isScrolledIntoView(this.nodeSkills)?(e.classList.remove("hidden"),e.classList.add("bar"),e.classList.add("basic")):(e.classList.remove("basic"),e.classList.remove("bar"),e.classList.add("hidden"))}),this.nodeAdv.forEach(e=>{this.isScrolledIntoView(this.nodeSkills)?(e.classList.remove("hidden"),e.classList.add("bar"),e.classList.add("advanced")):(e.classList.remove("advanced"),e.classList.remove("bar"),e.classList.add("hidden"))}),this.nodeExp.forEach(e=>{this.isScrolledIntoView(this.nodeSkills)?(e.classList.remove("hidden"),e.classList.add("bar"),e.classList.add("expert")):(e.classList.remove("expert"),e.classList.remove("bar"),e.classList.add("hidden"))})}isScrolledIntoView(e){let s=e.getBoundingClientRect(),i=s.top,t=s.bottom,d=i>=0&&t<=window.innerHeight;return d=i<window.innerHeight&&t>=1}}class Index{constructor(){new Hamburger,new Main_new}}document.addEventListener("DOMContentLoaded",()=>{new Index});