class Hamburger{constructor(){this.hamburgerDiv=document.querySelector("#icon"),this.divs=document.querySelector("#divTop"),this.divHead=document.querySelector(".head"),this.menuMobile=document.querySelector("#menuMobile"),this.hamburgerDiv.addEventListener("click",this.abrirMenu.bind(this)),document.addEventListener("scroll",this.animationHamburguer.bind(this))}abrirMenu(){this.divs.classList.toggle("open"),this.menuMobile.classList.toggle("active")}animationHamburguer(e){e.target.scrollingElement.scrollTop>0?(this.divHead.classList.add("animate"),this.menuMobile.classList.remove("active"),this.divs.classList.remove("open")):this.divHead.classList.remove("animate")}}class Main_new{constructor(){document.addEventListener("scroll",this.animarSkills.bind(this)),this.nodeBas=document.querySelectorAll(".nodeBas"),this.nodeAdv=document.querySelectorAll(".nodeAdv"),this.nodeExp=document.querySelectorAll(".nodeExp")}animarSkills(){this.nodeBas.forEach(e=>{this.isScrolledIntoView(e)?(e.classList.remove("empty"),e.classList.add("basic")):(e.classList.remove("basic"),e.classList.add("empty"))}),this.nodeAdv.forEach(e=>{this.isScrolledIntoView(e)?(e.classList.remove("empty"),e.classList.add("advanced")):(e.classList.remove("advanced"),e.classList.add("empty"))}),this.nodeExp.forEach(e=>{this.isScrolledIntoView(e)?(e.classList.remove("empty"),e.classList.add("expert")):(e.classList.remove("expert"),e.classList.add("empty"))})}isScrolledIntoView(e){let s=e.getBoundingClientRect(),t=s.top,i=s.bottom;return t>=0&&i<=window.innerHeight}}class Index{constructor(){new Hamburger,new Main_new}}document.addEventListener("DOMContentLoaded",()=>{new Index});